import React, {useContext, useEffect, useState, useRef} from 'react';
import {Link, useNavigate} from "react-router-dom";
import MainContext from "../context/MainContext";
import {Modal, InputWrapper, Input, Button} from "@mantine/core";

const Navbar = () => {
    const {user, setUser} = useContext(MainContext)
    const nav = useNavigate()
    const [opened, setOpened] = useState(false);
    const [avatarModalErr, setAvatarModalErr] = useState(null)
    const avatarRef = useRef()
    function logOut() {
        localStorage.clear();
        setUser(undefined)
        nav('/login')
    }

    function addAvatar() {
        if (!avatarRef.current.value.includes('http')) return setAvatarModalErr('HTTP not included')
        const newObj = {
            email: user.email,
            avatar: avatarRef.current.value
        }
        setOpened(false)
        const options = {
            method: "POST",
            headers: {
                "content-type": "application/json"
            },
            body: JSON.stringify(newObj)
        }
        fetch('http://localhost:4000/addAvatar', options)
            .then(res => res.json())
            .then(data => {
                if (data.hasOwnProperty('reason')){
                    console.log(data.reason)
                }  else {
                    setUser(data)
                    alert('Avatar has been changed!')
                    nav('/')
                }
            })

    }

    return (
        <div className='navbar row'>
            <a onClick={() => nav('/')}>
                <img src="https://logodix.com/logo/324487.png" className='logo' alt=""/>
            </a>
            {user &&
                <>
                    <div style={{display: 'flex', gap: '10px', alignItems: 'center'}}>
                        <p>Welcome, {user.email}!</p>
                        <div className="avatar" onClick={() => setOpened(true)}>
                            {user?.avatar === 'none' ?
                            <img src="data:image/png;base64," alt="" className="avatar-img"/>
                            :
                                <img src={user.avatar}/>
                            }
                        </div>
                    </div>

                </>
            }


            <div className="navbar-buttons row">
                <p className='btn btn--header btn--myAccount'
                   onClick={() => nav('/myAccount/'+ user.email)}>My Account</p>
                <p className='btn btn--header'
                   onClick={() => nav('/createProduct')}>Create topic</p>

                <p className='btn btn--header' onClick={logOut} style={{display: !user ? 'none' : 'block'}}>Logout</p>
            </div>



            <Modal
                opened={opened}
                onClose={() => setOpened(false)}
                title="Change your avatar!"
            >
                <InputWrapper
                    id="input-demo"
                    label="Avatar link"
                    description="Please enter your new avatar's http link"
                    size="md"
                >
                    {avatarModalErr && <h3 style={{color: 'red'}}>{avatarModalErr}</h3>}
                    <Input id="input-demo" placeholder="Link goes here" ref={avatarRef}/>
                    <Button variant="light" color="indigo" onClick={() => addAvatar()}>
                        Upload
                    </Button>
                </InputWrapper>
            </Modal>

        </div>
    );
};



export default Navbar;